mapper.select:
SELECT DISTINCT hr.role_id, hr.role_name, hr.role_createuser, hr.role_createdate FROM demo_role AS hr LEFT JOIN demo_user AS hu ON hr.role_createuser = hu.user_id AND hu.user_status = 1
INNER JOIN demo_role_permission AS hrp ON hr.role_id = hrp.role_permission_rid AND hrp.role_permission_status = 1
INNER JOIN demo_user_role AS hur ON hr.role_id = hur.user_role_rid AND hur.user_role_status = 1
WHERE hr.role_status = 1
AND hr.role_id = #{role,jdbcType=BIGINT}
AND hur.user_role_uid = #{user,jdbcType=BIGINT}
AND hrp.role_permission_pid = #{permission,jdbcType=BIGINT}
LIMIT #{startIndex,jdbcType=BIGINT}, #{pageSize,jdbcType=BIGINT};

SELECT count(DISTINCT hr.role_id)
FROM demo_role AS hr

    INNER JOIN demo_role_permission AS hrp ON hr.role_id = hrp.role_permission_rid AND hrp.role_permission_status
    = 1


    INNER JOIN demo_user_role AS hur ON hr.role_id = hur.user_role_rid AND hur.user_role_status = 1

WHERE hr.role_status = 1

    AND hr.role_id = #{role,jdbcType=BIGINT}


    AND hur.user_role_uid = #{user,jdbcType=BIGINT}


    AND hrp.role_permission_pid = #{permission,jdbcType=BIGINT}
;
