mapper.select:
select

FROM demo_user AS hu
WHERE hu.user_status = 1
AND
(hu.user_nickname =#{account,jdbcType=VARCHAR}
OR hu.user_truename =#{account,jdbcType=VARCHAR}
OR hu.user_id =#{account,jdbcType=VARCHAR}
OR hu.user_email =#{account,jdbcType=VARCHAR}
OR hu.user_mobile =#{account,jdbcType=VARCHAR});
